public extension PrivyLivenessModule {
    static func create(with config: PrivyLivenessConfig, showResultPage: Bool, callback: LivenessCallBackType) -> UIViewController{
        return PrivyLivenessModule.create(
            environment: config.environment, // development, staging, production
            applicationId: config.applicationId,
            clientId: config.clientId,
            clientSecret: config.clientSecret,
            publicKey: config.publicKey,
            privateKey: config.privateKey,
            salt: config.salt,
            showResultPage: showResultPage,
            callback: callback)
    }
}
